// create tables:<br>&lt;?php
<br>$servername = "localhost";
<br>$username = "username";
<br>$password = "password";
<br>$dbname = "myDBPDO";
<br>
<br>try {
<br>&nbsp; &nbsp; $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);
<br>&nbsp; &nbsp; // set the PDO error mode to exception
<br>&nbsp; &nbsp; $conn-&gt;setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);
<br>
<br>&nbsp; &nbsp; // sql to create table
<br>&nbsp; &nbsp; $sql = "CREATE TABLE IF NOT EXISTS MyGuests (
<br>&nbsp; &nbsp; id INT(6) UNSIGNED AUTO_INCREMENT PRIMARY KEY,
<br>&nbsp; &nbsp; firstname VARCHAR(30) NOT NULL,
<br>&nbsp; &nbsp; lastname VARCHAR(30) NOT NULL,
<br>&nbsp; &nbsp; email VARCHAR(50),
<br>&nbsp; &nbsp; reg_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
<br>&nbsp; &nbsp; )";
<br>
<br>&nbsp; &nbsp; // use exec() because no results are returned
<br>&nbsp; &nbsp; $conn-&gt;exec($sql);
<br>&nbsp; &nbsp; echo "Table MyGuests created successfully";
<br>&nbsp; &nbsp; }
<br>catch(PDOException $e)
<br>&nbsp; &nbsp; {
<br>&nbsp; &nbsp; echo $sql . "&lt;br&gt;" . $e-&gt;getMessage();
<br>&nbsp; &nbsp; }
<br>
<br>$conn = null;
<br>?&gt;<br>
