// search in array of objects&nbsp;<br>let items = [
<br>&nbsp; &nbsp; {
<br>&nbsp; &nbsp; &nbsp; &nbsp; title: 'book1',
<br>&nbsp; &nbsp; &nbsp; &nbsp; name: 'ali',
<br>&nbsp; &nbsp; &nbsp; &nbsp; price: '300'
<br>&nbsp; &nbsp; },
<br>&nbsp; &nbsp; {
<br>&nbsp; &nbsp; &nbsp; &nbsp; title: 'book2',
<br>&nbsp; &nbsp; &nbsp; &nbsp; name: 'hosein',
<br>&nbsp; &nbsp; &nbsp; &nbsp; price: '200'
<br>&nbsp; &nbsp; },
<br>&nbsp; &nbsp; {
<br>&nbsp; &nbsp; &nbsp; &nbsp; title: 'book3',
<br>&nbsp; &nbsp; &nbsp; &nbsp; name: 'ashkan',
<br>&nbsp; &nbsp; &nbsp; &nbsp; price: 200
<br>&nbsp; &nbsp; }
<br>]
<br>function findItem () {
<br>&nbsp; &nbsp; return items.find((v, i) =&gt; {
<br>&nbsp; &nbsp; &nbsp; &nbsp; return v.title == 'book3'
<br>&nbsp; &nbsp; })
<br>}
<br>console.log(findItem());<br>
