index.js<div>----------------------------------------------------------</div><div>const express = require('express');
</div><div>const path = require('path');
</div><div>const app = express();
</div><div>app.set('view engine', 'vash');
</div><div>app.get('/:x', (req, res) =&gt; {
</div><div>&nbsp; &nbsp; res.render('ali', {url: req.url, data: ['ali', 'hasan']});
</div><div>});
</div><div>app.listen(1397);
</div><div>console.log('server is listening on port 1397');</div><div><br></div><div>ali.vash</div><div>----------------------------------------------------------</div><div>&lt;!DOCTYPE html&gt;
